import React from "react";
import "./NewExpense.css";
import ExpenseForm from "./ExpenseForm";
import ClosedExpenseForm from "./ClosedExpenseForm";

const NewExpense = (props) => {
    const saveExpenseDataHandler = (enteredData) => {
        const expenseData = {
            ...enteredData,
            id: Math.random().toString()
        };
        props.onAddExpense(expenseData);
    };

    const openExpenseForm = () => {
        formState = (
            <div className="new-expense">
                <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} onCancelForm={closeForm}/>
            </div>
        );
    }

    const closeForm = () => {
        formState = (
            <div className="new-expense">
                <ClosedExpenseForm openExpensesForm={openExpenseForm}/>
            </div>
        );
    }

    let formState = (<div className="new-expense">
        <ClosedExpenseForm openExpensesForm={openExpenseForm}/>
    </div>);

    return formState;
}

export default NewExpense;
